    return (
      <div className="App">
        <NavBar/>
        <Switch>

          <Route path={`/${this.infoItemLinkName}`} render={() => (
            <ItemInfo
              itemName={this.infoItemName}
              itemPrice={this.infoItemPrice}
                />
  )} />
          <Route exact path="/" render={(routeProps) => (
            <Items
                items={this.state.items}
