  render () {
    return (
      <div>
        <img src={`../resources/${this.props.item.linkName}.jpg`} alt=""/>
  <h1>{this.props.itemName}</h1>
  <h4>${this.props.itemPrice}</h4>
  <p>{this.itemDescription}</p>
  </div>
    )
  }
}

export default ItemInfo;
